import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-map-sidebar',
  templateUrl: './map-sidebar.component.html',
  styleUrls: ['./map-sidebar.component.css'],
})
export class MapSidebarComponent {
  @Input() panel: any;
}
<div>
  <h2>Panel Location</h2>
  <div>
    Latitude: {{ panel.Lat }}
    Longitude: {{ panel.Long }}
  </div>
  <div id="map">
    <!-- Use a map library (e.g., Google Maps, Leaflet) to display the panel location -->
  </div>
</div>
