import { Component, OnInit } from '@angular/core';
import { PanelService } from './panel.service';

@Component({
  selector: 'app-panel-list',
  templateUrl: './panel-list.component.html',
  styleUrls: ['./panel-list.component.css'],
})
export class PanelListComponent implements OnInit {
  panelList: any[] = [];

  constructor(private panelService: PanelService) {}

  ngOnInit(): void {
    this.fetchPanelList();
  }

  fetchPanelList(): void {
    const orgId = 3;
    this.panelService.getPanelList(orgId).subscribe(
      (response) => {
        if (response.statusCode === 200) {
          this.panelList = response.result;
        }
      },
      (error) => {
        console.error('Error fetching panel list:', error);
      }
    );
  }
}
<table>
  <thead>
    <tr>
      <th>Panel Name</th>
      <th>Mac Id</th>
      <th>Lat</th>
      <th>Long</th>
      <th>Location</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let panel of panelList">
      <td (click)="openSidebar(panel)">{{ panel.PanelName }}</td>
      <td>{{ panel.MacId }}</td>
      <td>{{ panel.Lat }}</td>
      <td>{{ panel.Long }}</td>
      <td (click)="openMapSidebar(panel)"><img src="location_icon.png" alt="Location Icon"></td>
    </tr>
  </tbody>
</table>
