import { Component, OnInit, AfterViewInit, ElementRef } from '@angular/core';
import { PanelService } from './panel.service';
import * as L from 'leaflet';

@Component({
  selector: 'app-panel-list',
  templateUrl: './panel-list.component.html',
  styleUrls: ['./panel-list.component.css'],
})
export class PanelListComponent implements OnInit, AfterViewInit {
  panelList: any[] = [];
  map!: L.Map;
  mapContainer!: HTMLElement;

  constructor(
    private panelService: PanelService,
    private elementRef: ElementRef
  ) {}

  ngOnInit(): void {
    this.fetchPanelList();
  }

  ngAfterViewInit(): void {
        this.mapContainer =
          this.elementRef.nativeElement.querySelector('#mapContainer');
              this.initializeMap();
  }

  fetchPanelList(): void {
    const orgId = 3;
    this.panelService.getPanelList(orgId).subscribe(
      (response) => {
        if (response.statusCode === 200) {
          this.panelList = response.result;
        }
      },
      (error) => {
        console.error('Error fetching panel list:', error);
      }
    );
  }

  selectedPanel: any = null;
  showMapPanel: boolean = false;

  openSidebar(panel: any) {
    this.selectedPanel = panel;
  }

  openMapPanel(panel: any) {
    this.selectedPanel = panel;
    this.showMapPanel = true;
  }

  closeSidebar() {
    this.selectedPanel = null;
    this.showMapPanel = false;
  }

  initializeMap() {
    this.map = L.map('map').setView(
      [this.selectedPanel.Lat, this.selectedPanel.Lng],
      13
    );
    console.log('map:', this.map);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution:
        'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
      maxZoom: 18,
    }).addTo(this.map);

    L.marker([this.selectedPanel.Lat, this.selectedPanel.Lng])
      .addTo(this.map)
      .bindPopup('Panel Location');
  }
}
<table>
  <tr>
    <th>Panel Name</th>
    <th>Mac Id</th>
    <th>Lat</th>
    <th>Long</th>
    <th>Location</th>
  </tr>
  <tr *ngFor="let panel of panelList">
    <td (click)="openSidebar(panel)">{{ panel.panel_name }}</td>
    <td>{{ panel.mac_id }}</td>
    <td>{{ panel.Lat }}</td>
    <td>{{ panel.Lng }}</td>
    <td><img src="../assets/location.png" (click)="openMapPanel(panel)" alt="Location Icon"  width="50" height="50"></td>
  </tr>
</table>

<div class="sidebar" *ngIf="selectedPanel">
  <h2>Panel Location</h2>
  <p><strong>Panel Name:</strong> {{ selectedPanel.panel_name }}</p>
  <p><strong>Mac Id:</strong> {{ selectedPanel.mac_id }}</p>
  <p><strong>Lat:</strong> {{ selectedPanel.Lat }}</p>
  <p><strong>Long:</strong> {{ selectedPanel.Lng }}</p>
  <div class="map-container" #map></div>
  <!-- <div class="map" style="height: 400px;"></div> -->
  <button (click)="closeSidebar()">Close</button>
</div>
